import React, {Component} from 'react';
import API from '../utils/API';

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class CreateOrder extends Component {
    constructor(props) {
        super(props);
        this.state = {
            transTypes: [],
            products: []
        }
    }

    getTransactionTypes = () => {
        API.getAllTransactionTypes()
            .then((dbResponse) => {
                this.setState({transTypes: dbResponse.data})
                // for (let id in dbResponse.data) {
                //     console.log(dbResponse.data[id].transaction_type);
                // }
            })
    }

    getProducts = () => {
        API.getAllProducts()
            .then((dbResponse) => {
                this.setState({product: dbResponse.data}, () => console.log())
            })
    }

    componentDidMount() {
        this.getTransactionTypes();
    }

    render() {
        return (
            <div>
                <Form>
                <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Transaction Type</Form.Label>
                        <Form.Control as="select">
                            {this.state.transTypes.map((transType, index) => 
                                <option key={index} value={transType.transaction_name} id={transType.transaction_type_id}>{transType.transaction_type}</option>
                            )}
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Product ID</Form.Label>
                        <Form.Control as="select">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="exampleForm.ControlTextarea1">
                        <Form.Label>Price</Form.Label>
                        <Form.Control as="textarea" rows="1" />
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control as="textarea" rows="1" />
                    </Form.Group>
                </Form>
                <Button variant="success">Order Now</Button>
            </div>
        )
    }
}

export default CreateOrder;
