import React, {Component} from 'react';
import API from '../utils/API';
import forecastModel from '../utils/forecastingModel';

import Table from 'react-bootstrap/Table';

class Forecast extends Component {
    constructor(props) {
        super(props);

    this.state = {
        unitsOrder: '',
        totalOrder: '',
        masterCarton: '',
        orderAmount: '',
        selectedProduct: ''
    }
}

// create an event which will send the data to 
handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
}

submitAssumptions = (e) => {
    e.preventDefault();

    let output = forecastModel.forecastOutput(parseFloat(this.state.unitsOrder), parseFloat(this.state.totalOrder), parseFloat(this.state.masterCarton));
    let product = document.getElementById("products");

    this.setState(
        {orderAmount: output, selectedProduct: product.options[product.selectedIndex].value}, 
        () => {
            API.getProduct(this.state.selectedProduct).then((data)=> console.log(data));
        });
}

    render() {
        return(
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>Assumption</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>Units Order</td>
                    <td><input type="text"></input></td>
                    <td>Otto</td>
                    <td>@mdo</td>
                    </tr>
                    <tr>
                    <td>Total Orders</td>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <td>Master Carton</td>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <td>Sales Growth</td>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <td>Lead Time</td>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>

                </tbody>
            </Table>
        )
    }
}

export default Forecast;
