import React, {Component} from 'react';
import API from '../utils/API';

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class CreateOrder extends Component {
    constructor(props) {
        super(props);
        this.state = {
            transTypes: [],
            products: [],
            price: 0,
            quantity: 0        }
    }

    getTransactionTypes = () => {
        API.getAllTransactionTypes()
            .then((dbResponse) => {
                this.setState({transTypes: dbResponse.data})
                // for (let id in dbResponse.data) {
                //     console.log(dbResponse.data[id].transaction_type);
                // }
            })
    }

    getProducts = () => {
        API.getAllProducts()
            .then((dbResponse) => {
                this.setState({products: dbResponse.data}, () => console.log(this.state.products));
            });
    }

    componentDidMount() {
        this.getTransactionTypes();
        this.getProducts();
    }

    handleInputChange = (e) => {
        e.persist();

        this.setState({[e.target.name]: e.target.value}, (output) => {
            console.log(e.target)
        });
    }

    orderFormData = (e) => {
        console.log('get order data')

    }

    submitOrder = (e) => {
        e.preventDefault();

        console.log('submit order')
    }

    render() {
        return (
            <div>
                <Container></Container>
                <Form>
                <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Transaction Type</Form.Label>
                        <Form.Control as="select">
                            {this.state.transTypes.map((transType, index) => 
                                <option key={index} value={transType.transaction_name} id={transType.transaction_type_id}>{transType.transaction_type}</option>
                            )}
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Product SKU</Form.Label>
                        <Form.Control as="select">
                        {this.state.products.map((product, index) => 
                            <option key={index} value={product.product_name} id={product.product_id}>{product.product_sku}</option>
                        )}
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="exampleForm.ControlTextarea1">
                        <Form.Label>Price</Form.Label>
                        <Form.Control as="textarea" rows="1" name="price" onChange={this.handleInputChange} />
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control as="textarea" rows="1" name="quantity" onChange={this.handleInputChange} />
                    </Form.Group>
                </Form>
                <Button variant="success" onClick={this.submitOrder}>Order Now</Button>
            </div>
        )
    }
}

export default CreateOrder;
